# Usage - python plot_qualitative_video.py, after giving it the input frame names, 60 gt boxes and 60 predicted boxes.
import numpy as np
import cv2

def draw_bounding_boxes(image, gt_box, pred_box, frame_idx):
    """
    Draw bounding boxes on the image.
    """
    # Convert image to RGB (if needed)
    image = cv2.cvtColor(image, cv2.COLOR_BGR2RGB)
    
    # Draw ground truth bounding box
    if gt_box is not None:
        color = (255, 255, 255) if frame_idx < 15 else (0, 255, 0)  # White for observation, green for prediction
        cv2.rectangle(image, (int(gt_box[0]), int(gt_box[1])), (int(gt_box[2]), int(gt_box[3])), color, 2)
    
    # Draw predicted bounding box
    if pred_box is not None:
        cv2.rectangle(image, (int(pred_box[0]), int(pred_box[1])), (int(pred_box[2]), int(pred_box[3])), (255, 0, 255), 2)
    
    return image

def create_video_from_frames(frame_paths, gt_boxes, pred_boxes, output_video_path, fps=30):
    """
    Create a video by processing each frame and adding bounding boxes.
    """
    # Get frame dimensions
    first_frame = cv2.imread(frame_paths[0])
    height, width, layers = first_frame.shape

    # Initialize video writer
    video = cv2.VideoWriter(output_video_path, cv2.VideoWriter_fourcc(*'mp4v'), fps, (width, height))

    total_frames = len(frame_paths)

    for idx in range(total_frames):
        frame_path = frame_paths[idx]
        image = cv2.imread(frame_path)
        
        gt_box = gt_boxes[idx] if idx < len(gt_boxes) else None
        pred_box = pred_boxes[idx - 15] if idx >= 15 and (idx - 15) < len(pred_boxes) else None
        
        image_with_boxes = draw_bounding_boxes(image, gt_box, pred_box, idx)
        video.write(cv2.cvtColor(image_with_boxes, cv2.COLOR_RGB2BGR))
    
    cv2.destroyAllWindows()
    video.release()

